import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import DataTable from "react-data-table-component";
import { useState } from "react";
import { useEffect } from "react";

function Users() {
  const [users, setUsers] = useState();
  const url = "https://jsonplaceholder.typicode.com/users";
  var columns = [
    {
      name: "Id",
      selector: (row) => row.id,
      sortable: true,
    },
    {
      name: "Name",
      selector: (row) => row.name,
      sortable: true,
    },
    {
      name: "UserName",
      selector: (row) => row.username,
      sortable: true,
    },
    {
      name: "City",
      selector: (row) => row.address.city,
      sortable: true,
    },
    {
      name: "Zipcode",
      selector: (row) => row.address.zipcode,
      sortable: true,
    },
    {
      name: "Phone",
      selector: (row) => row.phone,
      sortable: true,
    },
    {
      name: "CompanyName",
      selector: (row) => row.company.name,
      sortable: true,
    },
    {
      name: "Ubication",
      selector: (row) => {
        var ubi =
          "https://www.google.com/maps/@" +
          row.address.geo.lat +
          ", " +
          row.address.geo.lng;
        return <a href={ubi}>Ubication</a>;
      },
      sortable: true,
    },
  ];

  const fecthApi = async () => {
    const response = await fetch(url);
    const responseJSON = await response.json();
    setUsers(responseJSON);
  };

  useEffect(() => {
    fecthApi();
  }, []);

  return (
    <div className="table table-responsive table-hover table-striped">
      
      <div className="barraBusqueda">
        <input type="text" placeholder="Buscar"
      className="textField" name="busqueda" value={this.state.busqueda}
      onChange={this.onChange} ></input> <button type="button" className="btnBuscar"
      FontAwesomeIcon icon={faSearch}></button></div>


      <DataTable
        columns={columns}
        title="List user"
        data={users}
        defaultSortField="id"
        defaultSortAsc={false}
        pagination
        highlightOnHover
      />
    </div>
  );
}

export default Users;
