import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import DataTable from "react-data-table-component";
import { useState } from "react";
import { useEffect } from "react";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


export function Albums() {

    const [albums, SetAlbums] = useState();
    const url = "https://jsonplaceholder.typicode.com/albums";

    const fecthApi = async () =>{
        const response = await fetch(url);
        const responseJSON = await response.json();
        SetAlbums(responseJSON);
    }

    useEffect(() => {
        fecthApi();
      }, []);

    return(

        <>
        <table className="mx-auto w-75 mt-5 table table-hover table-striped ">
            <thead>
                <tr>
                    <th>iserId</th>
                    <th>id</th>
                    <th>title</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {
                    !albums ? <tr> <td colSpan="4">Loading...</td></tr>: 
                    albums.map((elemento, index)=>{
                        return <tr key={index}>
                            <td>{elemento.userId}</td>
                            <td>{elemento.id}</td>
                            <td>{elemento.title}</td>
                            <td><button className="btn btn-info btn-outline text-white">show photos</button></td>
                        </tr>
                    })
                }
            </tbody>
        </table>
        </>



    );
}


//modal para mostrar las fotos

export function Example() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch static backdrop modal
      </Button>

      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Modal title</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Understood</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
